	private static HashSet<Integer> toHashSet(int[] arr) {
		HashSet<Integer> hashset = new HashSet<Integer>(arr.length<<1); // sparse hashset -> less entries per list -> faster lookup!
		for (int i : arr) hashset.add(i);
		return hashset;
	}

	/**
	 * Return sqrt(n) if n is a square of an integer, or null otherwise.
	 * Uses the full square bit pattern test instead of the fastmod test, which turns out to be clearly faster.
	 * 
	 * @param n
	 * @param fastModExp the 2-exponent of the used "fastMod"
	 * @param size the number of mods to use for the test
	 * @return sqrt(n) or null
	 */
	private static BigInteger exactSqrt_withFullSquareBitPatternTest(BigInteger n, int fastModExp, int size) {
		if (n.equals(I_0)) return I_0;
		// check contained power of 2
		int lsb = n.getLowestSetBit();
		if ((lsb & 1) == 1) return null; // n has an odd power of 2 -> no square
		
		// Improvement over "fastMod" (modulus to a power of 2) test, thanks to Graeme Willoughby:
		// Write some number as (2^m)k ,where k is the odd number left after factoring out all the multiples of 2.
		// The square of this number is (2 ^(2m))k^2. Now we know that the square of an odd number is of the form 8N+1.
		// So the general form of any square number is (2^(2m))(8N+1) , with m,N=0,1,2...
		// -> If n is square, then it must have the binary representation n = <something>001<2m 0's>
		// -> If n is square, then it must satisfy (n/2^lsb) % 8 == 1.
		// This is a very fast test, and 86% of input values are determined to be non-square by this method,
		// which is everything you can get from examining bit patterns.
		if ((n.shiftRight(lsb).intValue() & 7) != 1) return null; // we need only the lowest three bits
		
		// list of subsequent mods
		int[] nextMods = mods[fastModExp];
		//LOG.debug("nextMods=" + nextMods + ", size=" + size);
		//if (size>=nextMods.length) throw new IllegalStateException("nextMods=" + nextMods + ", size=" + size);
		for (int i=1; i<size; i++) {
			int nextMod = nextMods[i];
			HashSet<Integer> nextSquareRests = squareRests[nextMod];
			BigInteger nextMod_big = BigInteger.valueOf(nextMod);
			int rest = n.mod(nextMod_big).intValue();
			if (!nextSquareRests.contains(rest)) return null; // rest = n % nextMod is no square rest
		}
		// Newton sqrt() required
		BigInteger[] isqrt = SqrtInt.iSqrt(n);
		return isqrt[0].equals(isqrt[1]) ? isqrt[0] : null;
	}
	
	/**
	 * Performance test.
	 * Each algorithm is test with the same NCOUNT test numbers in random order.
	 * Random order is required to avoid compiler optimizations in the basic sqrt() computation
	 * which would blur the performance results.
	 */
	private static void testPerformance() {
		for (int nBits = 60; ; nBits+=10) {
			// create testset with NCOUNT n having nBits bits.
			BigInteger[] testSet = new BigInteger[NCOUNT];
			int i=0;
			while (i < NCOUNT) {
				testSet[i] = new BigInteger(nBits, RNG);
				if (testSet[i].signum()>0 && testSet[i].bitLength()==nBits) i++; // exclude 0 from test set, assure correct bit size
			}

			TreeMap<Long, List<String>> duration_2_algLists = new TreeMap<Long, List<String>>();
			for (int fastModExp=3; fastModExp<=MAX_MODEXP; fastModExp++) {
				int maxSize = mods[fastModExp].length;
				if (MAX_SIZE_ONLY) {
					testSingleAlgorithm_withFullSquareBitPatternTest(fastModExp, maxSize, testSet, duration_2_algLists);
				} else {
					// test all sizes
					for (int size=1; size<=maxSize; size++) {
						testSingleAlgorithm_withFullSquareBitPatternTest(fastModExp, size, testSet, duration_2_algLists);
					}
				}
			}
			
			long startMillis, duration;
			
			startMillis = System.currentTimeMillis();
			for (BigInteger n : testSet) {
				SqrtExact.exactSqrt(n);
			}
			duration = System.currentTimeMillis() - startMillis;
			addToAlgorithmMap("master implementation", duration, duration_2_algLists);

			// test permanent SqrtInt
			startMillis = System.currentTimeMillis();
			for (BigInteger n : testSet) {
				SqrtInt.iSqrt(n);
			}
			duration = System.currentTimeMillis() - startMillis;
			addToAlgorithmMap("SqrtInt", duration, duration_2_algLists);
			
			LOG.info("Best algorithms for n with " + nBits + " bits size: ");
			int pos = 1;
			for (Long duration2 : duration_2_algLists.keySet()) {
				int count = 0;
				List<String> algList = duration_2_algLists.get(duration2);
				for (String algStr : algList) {
					LOG.info("#" + pos + ": " + algStr + " took " + duration2 + "ms");
					count++;
				}
				pos += count;
				if (pos > 50) break;
			}
		}
	}
	
	private static void testSingleAlgorithm_withFullSquareBitPatternTest(int fastModExp, int size, BigInteger[] testSet, TreeMap<Long, List<String>> duration_2_algLists) {
		String algStr = "exactSqrt(" + fastModExp + ", " + size + ", bitPattern)";
		long startMillis = System.currentTimeMillis();
		for (BigInteger n : testSet) {
			exactSqrt_withFullSquareBitPatternTest(n, fastModExp, size);
		}
		long duration = System.currentTimeMillis() - startMillis;
		addToAlgorithmMap(algStr, duration, duration_2_algLists);
	}

	private static void addToAlgorithmMap(String algStr, long duration, TreeMap<Long, List<String>> duration_2_algLists) {
		List<String> algList = duration_2_algLists.get(duration);
		if (algList==null) algList = new ArrayList<String>();
		algList.add(algStr);
		duration_2_algLists.put(duration, algList);
	}
	
	/**
	 * Stand-alone test.
	 * @param args ignored
	 */
	public static void main(String[] args) {
		ConfigUtil.initProject();
		testPerformance();
	}
